import React, { useState } from "react";

function Map() {
  // State to track the active marker
  const [activeMarker, setActiveMarker] = useState(null);

  // Marker positions (adjust as per your map's layout)
  const markerPositions = {
    x_ray: { top: "39%", left: "43%", color: "red", fontSize: "3rem", backgroundColor: "red", },
    neurology: { top: "45%", left: "60%", color: "blue", fontSize: "3rem", backgroundColor: "blue", },
    dental: { top: "45%", left: "24%", color: "yellow", fontSize: "3rem", backgroundColor: "yellow", },
    main: { top: "65%", left: "66%", color: "teal", fontSize: "3rem", backgroundColor: "teal", },
    cafe: { top: "67%", left: "40%", color: "purple", fontSize: "3rem", backgroundColor: "purple", },
    fun: { top: "26%", left: "56%", color: "orange", fontSize: "3rem", backgroundColor: "orange", },
  };

  return (
    <>
      {/* Containers */}
      <div className="w-full flex flex-wrap text-white">
        <div
          onClick={() => setActiveMarker("x_ray")}
          className="bg-orange-600 flex-1 min-w-[150px] sm:min-w-[200px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          X-Ray
        </div>
        <div
          onClick={() => setActiveMarker("neurology")}
          className="bg-blue-700 flex-1 min-w-[150px] sm:min-w-[200px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          Neurology
        </div>
        <div
          onClick={() => setActiveMarker("dental")}
          className="bg-yellow-400 flex-1 min-w-[150px] sm:min-w-[200px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          Dental
        </div>
        <div
          onClick={() => setActiveMarker("main")}
          className="bg-teal-400 flex-1 min-w-[150px] sm:min-w-[200px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          Main Door
        </div>
        <div
          onClick={() => setActiveMarker("cafe")}
          className="bg-purple-700 flex-1 min-w-[150px] sm:min-w-[200px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          Cafe
        </div>
        <div
          onClick={() => setActiveMarker("fun")}
          className="bg-red-600 flex-1 min-w-[150px] sm:min-w-[125px] md:min-w-[200px] lg:min-w-[200px] p-4 flex justify-center items-center cursor-pointer"
        >
          Fun Zone
        </div>
      </div>

      {/* Map */}
      <div className="relative w-full">
        <img
          src="/assets/images/hospital_map.jpg"
          alt="Hospital Map"
          className="h-full w-full"
        />
        {Object.keys(markerPositions).map((key) => (
          <div
            key={key}
            className={`absolute ${
              activeMarker === key ? "block" : "hidden"
            }`}
            style={{
              top: markerPositions[key].top,
              left: markerPositions[key].left,
              color: markerPositions[key].color,
              fontSize: markerPositions[key].fontSize,
              transform: "translate(-50%, -100%)",

            }}
          >
            <div className="relative">
              <i className="fa-solid fa-map-pin"></i>
              <div className="absolute top-[-45px] left-[-20%] text-lg w-[120px] text-white px-3 py-1 rounded shadow-lg" style={{
              backgroundColor: markerPositions[key].backgroundColor,

            }}>
                {key.replace("_", " ")}
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}

export default Map;
