import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import "./App.css";

// Pages & Components
import Navigation from "./components/Navigation";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/dashboard/Dashboard";
import Account from "./pages/dashboard/Account";
import Games from "./pages/dashboard/Games";
import Map from "./pages/Map";

function App() {
  // Track login state and current user
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  // Check localStorage on mount to persist login
  useEffect(() => {
    const token = localStorage.getItem("token");
    const storedUser = localStorage.getItem("user");
    if (token && storedUser) {
      setIsLoggedIn(true);
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setUser(null);
  };

  // Login handler
  const handleLogin = (userData) => {
    localStorage.setItem("user", JSON.stringify(userData));
    // You can also store a token here if using JWT
    localStorage.setItem("token", "dummy-token"); // Replace with real token
    setIsLoggedIn(true);
    setUser(userData);
  };

  return (
    <Router>
      <div className="App min-h-screen bg-gray-100">
        {/* Navigation bar */}
        <Navigation
          isLoggedIn={isLoggedIn}
          user={user}
          handleLogout={handleLogout}
        />

        {/* Main content */}
        <main className="p-4">
          <Routes>
            {/* Public pages */}
            <Route path="/" element={<Home />} />

            {/* Login & Register (redirect if already logged in) */}
            <Route
              path="/login"
              element={
                isLoggedIn ? (
                  <Navigate to="/dashboard" replace />
                ) : (
                  <Login handleLogin={handleLogin} />
                )
              }
            />
            <Route
              path="/register"
              element={
                isLoggedIn ? <Navigate to="/dashboard" replace /> : <Register />
              }
            />

            {/* Protected pages */}
            <Route
              path="/dashboard"
              element={
                isLoggedIn ? <Dashboard /> : <Navigate to="/login" replace />
              }
            />
            <Route
              path="/account"
              element={
                isLoggedIn ? <Account /> : <Navigate to="/login" replace />
              }
            />
            <Route
              path="/games"
              element={
                isLoggedIn ? <Games /> : <Navigate to="/login" replace />
              }
            />

            {/* Map can be public or protected; choose here */}
            <Route
              path="/map"
              element={isLoggedIn ? <Map /> : <Navigate to="/login" replace />}
            />

            {/* Catch-all redirect to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
