import React, { useState, useEffect } from "react";
import "./App.css";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

// Pages & Components
import Navigation from "./components/Navigation";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Map from "./pages/Map";
import Dashboard from "./pages/dashboard/Dashboard";
import Account from "./pages/dashboard/Account";
import Games from "./pages/dashboard/Games";

function App() {
  // Track login state
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);

  // Check localStorage for existing session
  useEffect(() => {
    const token = localStorage.getItem("token"); // Optional if you use JWT
    const storedUser = localStorage.getItem("user");

    if (token && storedUser) {
      setIsLoggedIn(true);
      setUser(JSON.parse(storedUser));
    }
  }, []);

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setIsLoggedIn(false);
    setUser(null);
  };

  // Login handler
  const handleLogin = (userData) => {
    localStorage.setItem("user", JSON.stringify(userData));
    setIsLoggedIn(true);
    setUser(userData);
  };

  return (
    <Router>
      <div className="App flex flex-col min-h-screen">
        {/* Navigation */}
        <Navigation
          isLoggedIn={isLoggedIn}
          user={user}
          handleLogout={handleLogout}
        />

        {/* Main content */}
        <main className="flex-1">
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Home />} />
            <Route
              path="/login"
              element={
                isLoggedIn ? (
                  <Navigate to="/dashboard" replace />
                ) : (
                  <Login handleLogin={handleLogin} />
                )
              }
            />
            <Route
              path="/register"
              element={
                isLoggedIn ? <Navigate to="/dashboard" replace /> : <Register />
              }
            />

            {/* Protected Routes */}
            <Route
              path="/dashboard"
              element={
                isLoggedIn ? <Dashboard /> : <Navigate to="/login" replace />
              }
            />
            <Route
              path="/account"
              element={
                isLoggedIn ? <Account /> : <Navigate to="/login" replace />
              }
            />
            <Route
              path="/games"
              element={
                isLoggedIn ? <Games /> : <Navigate to="/login" replace />
              }
            />
            <Route
              path="/map"
              element={isLoggedIn ? <Map /> : <Navigate to="/login" replace />}
            />

            {/* Catch-all: redirect unknown routes to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
