import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const buddies = [
  { name: "Dog", image: "/assets/buddies/dog.png" },
  { name: "Cat", image: "/assets/buddies/cat.png" },
  { name: "Rabbit", image: "/assets/buddies/rabbit.png" },
  { name: "Panda", image: "/assets/buddies/panda.png" },
];

function Register() {
  const [hospitalNumber, setHospitalNumber] = useState("");
  const [password, setPassword] = useState("");
  const [selectedBuddy, setSelectedBuddy] = useState(buddies[0]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:5001/register", {
        hospital_number: hospitalNumber,
        password,
        buddy: selectedBuddy, // send buddy info to backend
      });

      if (response.status === 200) {
        navigate("/login");
      }
    } catch (err) {
      setError("Registration failed");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full bg-white p-8 rounded-2xl shadow">
        <h2 className="text-center text-2xl font-bold text-gray-800 mb-6">
          Register
        </h2>
        <form className="space-y-4" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Hospital Number"
            value={hospitalNumber}
            onChange={(e) => setHospitalNumber(e.target.value)}
            required
            className="w-full border px-4 py-2 rounded-md outline-blue-600"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="w-full border px-4 py-2 rounded-md outline-blue-600"
          />

          {/* Buddy Selection */}
          <div>
            <h4 className="text-gray-700 font-medium mb-2">
              Choose Your Hospital Buddy
            </h4>
            <div className="flex space-x-4">
              {buddies.map((buddy) => (
                <div
                  key={buddy.name}
                  onClick={() => setSelectedBuddy(buddy)}
                  className={`cursor-pointer p-2 rounded-md border-2 ${
                    selectedBuddy.name === buddy.name
                      ? "border-blue-600"
                      : "border-gray-200"
                  }`}
                >
                  <img
                    src={buddy.image}
                    alt={buddy.name}
                    className="w-16 h-16 object-contain"
                  />
                  <p className="text-center text-sm mt-1">{buddy.name}</p>
                </div>
              ))}
            </div>
          </div>

          {error && <p className="text-red-600">{error}</p>}

          <button
            type="submit"
            className="w-full py-2 px-4 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700"
          >
            Register
          </button>
        </form>
      </div>
    </div>
  );
}

export default Register;
